// app/components/Certifications.jsx
"use client";

import Link from "next/link";

const accent = { color: "#22E19D" }; // breadcrumb accent

export default function Certifications() {
  return (
    <section className="relative overflow-hidden">
      {/* Subtle BG wash like screenshot */}
      <div
        className="pointer-events-none absolute inset-0"
        style={{
          background:
            "radial-gradient(100% 70% at 10% 95%, #30E29D22 0%, #201DA733 70%, #0B0F1722 100%)",
          opacity: 0.55,
        }}
      />

      <div className="relative mx-auto max-w-[1300px] px-4 sm:px-6 pt-10 sm:pt-14 pb-12">
        {/* Breadcrumb */}
        <div className="text-[15px] text-[#B9C7D6] mb-3 font-['Graphikthin']">
          Our courses -{" "}
          <span
            style={{
              background:
                "linear-gradient(90deg,  #30E29D   0%, #201DA7 147.01%)",
              WebkitBackgroundClip: "text",
              WebkitTextFillColor: "transparent",
              backgroundClip: "text",
            }}
          >
            Certification programs
          </span>
        </div>

        {/* Heading */}
        <h1 className="text-[#E5E5E5] font-['Graphikthin'] font-normal tracking-[-0.018em] leading-[1.09] text-[38px] sm:text-[52px] md:text-[64px]">
          Get certified. get internship
          <br />
          experience. get job-ready.
        </h1>

        {/* Subhead */}
        <p className="mt-4 text-[16px] text-[#E0E0E0] font-normal mb-8 max-w-xl">
          Build in-demand skills through live projects and internship experience
          that strengthen your resume.Popular Certification
        </p>

        {/* Responsive grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 max-lg:justify-center max-lg:items-center gap-4 mb-10">
          {certs.map((c) => (
            <CertCard key={c.title} {...c} />
          ))}
        </div>

        {/* CTA */}
        <div className="flex justify-center">
          <Link href="/hire-from-us">
            <button className="group rounded-[8px] hover:bg-[#30E29D] hover:text-[#292929] border border-[#30E29D] px-7 py-2 text-[16px] text-[#30E29D] transition-colors hover:bg-[#22e19d10] hove:text-white">
              Explore All Certification Programs
            </button>
          </Link>
        </div>
      </div>
    </section>
  );
}

/* ==== Individual Card w/Responsive Glow Gradient ==== */
function CertCard({ icon, title, gradient }) {
  return (
    <div
      className="rounded-[10px] h-[215px] w-[305px] bg-[#13181e]/80 shadow-md transition-[transform,box-shadow] duration-150 hover:scale-[1.025] hover:shadow-lg backdrop-blur-[1.5px] p-0.5"
      style={{
        background: gradient,
      }}
    >
      <div className="flex justify-between flex-col h-full rounded-[9px] bg-[#0f1220cc] px-4 py-5 min-h-[120px]">
        <div className="mb-3 w-[85px] h-[55px]">{icon}</div>
        <div className="text-[17px] text-[#E0E0E0] font-normal font-['Graphikthin'] ">
          {title}
        </div>
      </div>
    </div>
  );
}

